import React, { Component } from 'react'

import { Link } from 'react-router-dom'
import { Grid, Button, Toast, Modal } from 'antd-mobile'

import { BASE_URL, rt } from '../../utils/axios'

import styles from './index.module.css'
import { isAuth, removeToken } from '../../utils'
import { getUserInfo, logout } from '../../utils/api/user'

const alert = Modal.alert;
// 菜单数据
const menus = [
  { id: 1, name: '我的收藏', iconfont: 'icon-coll', to: '/favorate' },
  { id: 2, name: '我的出租', iconfont: 'icon-ind', to: '/rent' },
  { id: 3, name: '看房记录', iconfont: 'icon-record' },
  {
    id: 4,
    name: '成为房主',
    iconfont: 'icon-identity'
  },
  { id: 5, name: '个人资料', iconfont: 'icon-myinfo' },
  { id: 6, name: '联系我们', iconfont: 'icon-cust' }
]

// 默认头像
const DEFAULT_AVATAR = BASE_URL + '/img/profile/avatar.png'

export default class Profile extends Component {
  state = {
    isLogin: isAuth(),
    userInfo: {}
  }

  componentDidMount() {
    this.getUserInfo()
  }
  // 获取用户数据
  getUserInfo = async () => {
    const { isLogin } = this.state;
    if (isLogin) {
      let res = await getUserInfo()
      console.log(res);
      if (res.status === 200) {
        // 处理图片路径
        res.data.avatar = BASE_URL + res.data.avatar;
        this.setState({
          userInfo: res.data
        })
      } else {
        removeToken();
        rt.history.push('/login');
        Toast.info(res.description);
      }
    }
  }

  // 退出登录
  logout = () => {
    alert('提示', '确定退出吗？', [
      { text: '取消' },
      {
        text: '确定', onPress: async () => {
          let res = await logout();
          console.log(res)
          if (res.status === 200) {
            removeToken();
            this.setState({
              isLogin: false,
              userInfo: {}
            })
          } else {
            this.props.history.push('/login')
          }
        }
      },
    ])
  }

  // 渲染用户信息
  renderUser() {
    const { history } = this.props;
    const { userInfo: { nickname, avatar } } = this.state;
    return (<div className={styles.title}>
      <img
        className={styles.bg}
        src={BASE_URL + '/img/profile/bg.png'}
        alt="背景图"
      />
      <div className={styles.info}>
        <div className={styles.myIcon}>
          <img className={styles.avatar} src={avatar || DEFAULT_AVATAR} alt="icon" />
        </div>
        <div className={styles.user}>
          <div className={styles.name}>{nickname || '游客'}</div>
          {
            this.state.isLogin ? (
              <>
                <div className={styles.auth}>
                  <span onClick={this.logout}>退出</span>
                </div>
                <div className={styles.edit}>
                  编辑个人资料
    <span className={styles.arrow}>
                    <i className="iconfont icon-arrow" />
                  </span>
                </div>
              </>) : (<div className={styles.edit}>
                <Button
                  type="primary"
                  size="small"
                  inline
                  onClick={() => history.push('/login')}
                >
                  去登录
              </Button>
              </div>)
          }
        </div>
      </div>
    </div>)
  }

  render() {
    return (
      <div className={styles.root}>
        {/* 个人信息 */}
        {
          this.renderUser()
        }

        {/* 九宫格菜单 */}
        <Grid
          data={menus}
          columnNum={3}
          hasLine={false}
          renderItem={item =>
            item.to ? (
              <Link to={item.to}>
                <div className={styles.menuItem}>
                  <i className={`iconfont ${item.iconfont}`} />
                  <span>{item.name}</span>
                </div>
              </Link>
            ) : (
                <div className={styles.menuItem}>
                  <i className={`iconfont ${item.iconfont}`} />
                  <span>{item.name}</span>
                </div>
              )
          }
        />

        {/* 加入我们 */}
        <div className={styles.ad}>
          <img src={BASE_URL + '/img/profile/join.png'} alt="" />
        </div>
      </div>
    )
  }
}
